//Importacion
import React from 'react';
import Button from './components/Button';
import './App.css';
import Result from './components/Result';
//Funcion Flecha o Arrow Function
const App =()=>{
    //Funciones para componentes
    const clickHandlerFunction=(text)=>{
        console.log(text)
    }
    const clickOperation=(text)=>{
        console.log(text)
    }
    const clickEqualds=(text)=>{
        console.log(text)
    }

    //lo que ejecuta la funcion
    return(
        <main className='react-calculator'>
            <Result 
            value = {"0"} />
            <div className="numbers">
                <Button text="1" cliklHandler={clickHandlerFunction}/>
                <Button text="2" cliklHandler={clickHandlerFunction}/>
                <Button text="3" cliklHandler={clickHandlerFunction}/>
                <Button text="4" cliklHandler={clickHandlerFunction}/>
                <Button text="5" cliklHandler={clickHandlerFunction}/>
                <Button text="6" cliklHandler={clickHandlerFunction}/>
                <Button text="7" cliklHandler={clickHandlerFunction}/>
                <Button text="8" cliklHandler={clickHandlerFunction}/>
                <Button text="9" cliklHandler={clickHandlerFunction}/>
                <Button text="0" cliklHandler={clickHandlerFunction}/>
            </div>
            <div className="functions">
                <button>Clear</button>
                <button>Remove</button>
            </div>
            <div className="math-operations">
                <Button 
                    text="+" 
                    cliklHandler={clickOperation}
                />
                <button>-</button>
                <button>*</button>
                <button>/</button>
                <Button 
                    text="=" 
                    cliklHandler={clickEqualds}
                />
            </div>
        </main> 
    )
}
//Exportacion
export default App;
