//Importacion
import React from 'react';
import Button from './components/Button';
import './App.css';
import Result from './components/Result';
//Funcion Flecha o Arrow Function
const App =()=>{
    //lo que ejecuta la funcion
    return(
        <main className='react-calculator'>
            <Result 
            value = {"0"} />
            <div className="numbers">
                <Button 
                    text="1" 
                    clickHandler={(num)=>{
                       console.log(num); 
                    }}
                />
                <button>2</button>
                <button>3</button>
                <button>4</button>
                <button>5</button>
                <button>6</button>
                <button>7</button>
                <button>8</button>
                <button>9</button>
                <button>0</button>
            </div>
            <div className="functions">
                <button>Clear</button>
                <button>Remove</button>
            </div>
            <div className="math-operations">
                <button>+</button>
                <button>-</button>
                <button>*</button>
                <button>/</button>
                <button>=</button>
            </div>
        </main> 
    )
}
//Exportacion
export default App;
