//Importacion
import React,{useState} from 'react';
import Numbers from './components/Numbers';
import Functions from './components/Functions';
import MathOperations from './components/MathOperations';
import './App.css';
import Result from './components/Result';
//Funcion Flecha o Arrow Function
const App =()=>{
    
    const [texto,functionModificaTexto]=useState("")
    //arrayYextoFuncionMedificaTexto=>["hola",funcion]
    //1er posicion: valor que esta por defecto inicialmente
    //2da posicion: funcion que me permite modificar el valor por defecto
    //lo que ejecuta la funcion
    return(
        <main className='react-calculator'>
            <Result 
            value = {texto} />
            <Numbers onClickNumber={number =>{
                    console.log(number)
                    functionModificaTexto(number)
                    }
                }
            />
            <Functions 
                onContentClear={( )=>
                    console.log('contenido borrado')
                }
                onDelete={()=>
                    console.log('borrando caracter')
                }
            />
            <MathOperations 
                onClickOperation={operation=>
                    console.log(operation)
                }
                onClickEqual={equal=>
                    console.log('equal',equal)
                }
            />
        </main> 
    )
}
//Exportacion
export default App;
